%GD plotter% Load datadata=load('ex1data1.txt');X = data(:,1);m=length(X);X=[ones(m,1), data(:,1)];y=data(:,2);%theta_init=zeros(2,1);theta_init=[-8;3];iterations=800;alpha=0.011;% Compute GD[theta, J, theta_history]= gradientDescent(X,y,theta_init, alpha, iterations);% Simple plotterrange=1:iterations;subplot(2,2,1);plot(range,theta_history(:,1), 'gx', 'MarkerSize',5);hold on;plot(range, theta_history(:,2), 'rx','MarkerSize',5);plot(range, J, 'bo', 'MarkerSize',12);legend('theta0','theta1','J'); hold off;% Complex plottertheta0_vals = linspace(-10,0,100);theta1_vals = linspace(-1,4,100);J_vals = zeros(length(theta0_vals), length(theta1_vals));% compute Jfor i=1:length(theta0_vals)  for j=1:length(theta1_vals)    t=[theta0_vals(i); theta1_vals(j)];    J_vals(i,j) = computeCost(X,y,t);
  endfor
endfor
% mesh grid plotterJ_vals = J_vals';%figure;subplot(2,2,2);plot3(theta_history(:,1), theta_history(:,2), J, 'r');surf(theta0_vals, theta1_vals, J_vals);xlabel('\theta_0'); ylabel('\theta_1');%figure;subplot(2,2,3);contour(theta0_vals, theta1_vals, J_vals, logspace(-2,3,100))xlabel('\theta_0'); ylabel('\theta_1');hold on;theta_history = [theta_init';theta_history]; % include starting values in the plotplot(theta_history(:,1),theta_history(:,2), 'b:o', 'MarkerSize',5);plot(theta(1), theta(2), 'rx', 'MarkerSize',10, 'LineWidth',2);hold off;